import React, { useState } from 'react';
import { Row, Col, Card, Toast, ToastContainer, Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import classNames from 'classnames';

// components
import PageTitle from '../../components/PageTitle';

// images
import user from '../../assets/images/users/avatar-2.jpg';
import user2 from '../../assets/images/users/avatar-4.jpg';
import user3 from '../../assets/images/users/avatar-5.jpg';
import user4 from '../../assets/images/users/avatar-6.jpg';

const colors: string[] = ['primary', 'secondary', 'success', 'danger', 'warning', 'info', 'light', 'dark'];

const DefaultToasts = () => {
    const [show, setShow] = useState<boolean>(true);
    const [showTranslucent, setShowTranslucent] = useState<boolean>(true);
    const [showPlacement, setShowPlacement] = useState<boolean>(true);

    const [stacked, setStacked] = useState([
        {
            title: 'Richard',
            avatar: user2,
            time: 'just now',
            desc: 'See? Just like this.',
        },
        {
            title: 'Edward',
            avatar: user3,
            time: '2 seconds ago',
            desc: 'Heads up, toasts will stack automatically',
        },
    ]);

    /*
     * handle close
     */
    const handleClose = (index: number) => {
        const list = [...stacked];
        list.splice(index, 1);
        setStacked(list);
    };

    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">Bootstrap Toasts</h4>
                <p className="text-muted">
                    Push notifications to your visitors with a toast, a lightweight and easily customizable alert
                    message.
                </p>

                <Row>
                    <Col md={6}>
                        <h5 className="font-14 mb-1">Basic</h5>
                        <p className="text-muted">
                            Toasts are as flexible as you need and have very little required markup. At a minimum, we
                            require a single element to contain your toasted content and strongly encourage a dismiss
                            button.
                        </p>
                        <div className="p-3">
                            <Toast onClose={() => setShow(false)} show={show} delay={8000} autohide>
                                <Toast.Header>
                                    <img src={user} alt="brand-logo" height="28" className="me-1 rounded" />
                                    <strong className="me-auto">Alfredo</strong>
                                    <small>11 mins ago</small>
                                </Toast.Header>
                                <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
                            </Toast>
                        </div>
                    </Col>

                    <Col md={6}>
                        <h5 className="font-14 mb-1">Translucent</h5>
                        <p className="text-muted">
                            Toasts are slightly translucent, too, so they blend over whatever they might appear over.
                            For browsers that support the backdrop-filter CSS property, well also attempt to blur the
                            elements under a toast.
                        </p>

                        <div className="p-3 bg-light">
                            <Toast
                                onClose={() => setShowTranslucent(false)}
                                show={showTranslucent}
                                delay={5000}
                                autohide
                            >
                                <Toast.Header>
                                    <img src={user2} alt="brand-logo" height="28" className="me-1" />
                                    <strong className="me-auto">Andrew</strong>
                                    <small className="ms-5">11 mins ago</small>
                                </Toast.Header>
                                <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
                            </Toast>
                        </div>
                    </Col>
                </Row>

                {/* stacked */}
                <Row>
                    <Col md={6} className="mt-4">
                        <h5 className="font-14 mb-1">Stacking</h5>
                        <p className="text-muted">
                            When you have multiple toasts, we default to vertiaclly stacking them in a readable manner.
                        </p>
                        <div className="p-3">
                            <div
                                aria-live="polite"
                                aria-atomic="true"
                                style={{ position: 'relative', minHeight: '200px' }}
                            >
                                <div className="toast-container" style={{ position: 'absolute', top: 0, right: 0 }}>
                                    {(stacked || []).map((item, index) => {
                                        return (
                                            <Toast key={index} onClose={() => handleClose(index)} delay={7000} autohide>
                                                <Toast.Header>
                                                    <img
                                                        src={item.avatar}
                                                        alt="brand-logo"
                                                        height="28"
                                                        className="me-1 rounded"
                                                    />
                                                    <strong className="me-auto">{item.title}</strong>
                                                    <small className="ms-5">{item.time}</small>
                                                </Toast.Header>
                                                <Toast.Body>{item.desc}</Toast.Body>
                                            </Toast>
                                        );
                                    })}
                                </div>
                            </div>
                        </div>
                    </Col>

                    <Col md={6} className="mt-4">
                        <h5 className="font-14 mb-1">Placement</h5>
                        <p className="text-muted">
                            Place toasts with custom CSS as you need them. The top right is often used for
                            notifications, as is the top middle. If youre only ever going to show one toast at a time,
                            put the positioning styles right on the <code>.toast</code>.
                        </p>

                        <div className="p-3">
                            <div
                                aria-live="polite"
                                aria-atomic="true"
                                className="d-flex justify-content-center align-items-center"
                                style={{ minHeight: '200px' }}
                            >
                                <Toast onClose={() => setShowPlacement(false)} show={showPlacement}>
                                    <Toast.Header>
                                        <img src={user4} alt="brand-logo" height="28" className="me-1 rounded" />
                                        <strong className="me-auto">Ronnie</strong>
                                        <small className="ms-5">1 mins ago</small>
                                    </Toast.Header>
                                    <Toast.Body>Heads up, toasts will stack automatically</Toast.Body>
                                </Toast>
                            </div>
                        </div>
                    </Col>
                </Row>
            </Card.Body>
        </Card>
    );
};

const CustomToast = () => {
    const [showCustom1, setShowCustom1] = useState<boolean>(true);
    const [showCustom2, setShowCustom2] = useState<boolean>(true);
    const [showCustom3, setShowCustom3] = useState<boolean>(true);
    const [showCustom4, setShowCustom4] = useState<boolean>(true);

    return (
        <>
            <Card>
                <Card.Body>
                    <h4 className="header-title">Custom content</h4>
                    <p className="text-muted">
                        Alternatively, you can also add additional controls and components to toasts.
                    </p>
                    <Toast
                        className="d-flex align-items-center mt-4"
                        show={showCustom1}
                        onClose={() => setShowCustom1(false)}
                        delay={6000}
                        autohide
                    >
                        <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
                        <Button
                            variant=""
                            onClick={() => setShowCustom1(false)}
                            className="btn-close ms-auto me-2"
                        ></Button>
                    </Toast>
                    <Toast
                        className="d-flex align-items-center text-white bg-primary border-0 mt-4"
                        show={showCustom2}
                        onClose={() => setShowCustom2(false)}
                        delay={10000}
                        autohide
                    >
                        <Toast.Body>Hello, world! This is a toast message.</Toast.Body>

                        <Button
                            variant=""
                            onClick={() => setShowCustom2(false)}
                            className="btn-close btn-close-white ms-auto me-2"
                        ></Button>
                    </Toast>
                    <Toast
                        className="mt-4"
                        show={showCustom3}
                        onClose={() => setShowCustom3(false)}
                        delay={12000}
                        autohide
                    >
                        <Toast.Body>
