import React, { useState } from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Select from 'react-select';
import { Typeahead } from 'react-bootstrap-typeahead';
import 'react-bootstrap-typeahead/css/Typeahead.css';
import MaskedInput from 'react-text-mask';

// components
import PageTitle from '../../components/PageTitle';
import MintonDatepicker from '../../components/Datepicker';

const ReactSelect = () => {
    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">
                    <Link
                        rel="noreferrer"
                        to={{ pathname: 'https://github.com/JedWatson/react-select' }}
                        target="_blank"
                    >
                        React select
                    </Link>
                </h4>
                <p className="mb-1 mt-3 fw-bold">Single Selection</p>
                <p className="text-muted fs-14">React-Select based Select element</p>
                <Select
                    className="react-select react-select-container"
                    classNamePrefix="react-select"
                    options={[
                        { value: 'chocolate', label: 'Chocolate' },
                        { value: 'strawberry', label: 'Strawberry' },
                        { value: 'vanilla', label: 'Vanilla' },
                    ]}
                ></Select>

                <p className="mb-1 mt-3 fw-bold">Multiple Selection</p>
                <p className="text-muted fs-14">React-Select based Select (Multiple) element</p>
                <Select
                    isMulti={true}
                    options={[
                        { value: 'chocolate', label: 'Chocolate' },
                        { value: 'strawberry', label: 'Strawberry' },
                        { value: 'vanilla', label: 'Vanilla' },
                    ]}
                    className="react-select react-select-container"
                    classNamePrefix="react-select"
                ></Select>
            </Card.Body>
        </Card>
    );
};

interface OptionTypes {
    id: number | string;
    value: string;
    label: string;
}
const Typeaheads = () => {
    const [singleSelections, setSingleSelections] = useState<OptionTypes[]>([]);
    const [multiSelections, setMultiSelections] = useState<OptionTypes[]>([]);

    const options: Array<OptionTypes> = [
        { id: 1, value: 'chocolate', label: 'Chocolate' },
        { id: 2, value: 'strawberry', label: 'Strawberry' },
        { id: 3, value: 'vanilla', label: 'Vanilla' },
    ];

    const onChangeSingleSelection = (selected: any[]) => {
        setSingleSelections(selected);
    };

    const onChangeMultipleSelection = (selected: OptionTypes[]) => {
        setMultiSelections(selected);
    };

    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">
                    <Link
                        rel="noreferrer"
                        to={{ pathname: 'http://ericgio.github.io/react-bootstrap-typeahead/' }}
                        target="_blank"
                    >
                        Typeahead
                    </Link>
                </h4>
                <p className="mb-1 mt-3 fw-bold">Single Selection</p>
                <p className="text-muted fs-14">Typeahead based Select element</p>
                <Typeahead
                    id="select2"
                    labelKey={'label'}
                    multiple={false}
                    onChange={onChangeSingleSelection}
                    options={options}
                    placeholder="Choose a state..."
                    selected={singleSelections}
                />

                <p className="mb-1 mt-3 fw-bold">Multiple Selection</p>
                <p className="text-muted fs-14">Typeahead based Select (Multiple) element</p>
                <Typeahead
                    id="select3"
                    labelKey="label"
                    multiple
                    onChange={onChangeMultipleSelection}
                    options={options}
                    placeholder="Choose a state..."
                    selected={multiSelections}
                />
            </Card.Body>
        </Card>
    );
};

const DatePickers = () => {
    const [selectedDate1, setSelectedDate1] = useState<Date>(new Date());
    const [selectedDate2, setSelectedDate2] = useState<Date>(new Date());
    const [selectedDate3, setSelectedDate3] = useState<Date>(new Date());
    const [selectedDate4, setSelectedDate4] = useState<Date>(new Date());
    const [selectedDate5, setSelectedDate5] = useState<Date>(new Date());
    const [dateRange, setDateRange] = useState<any>([new Date(), new Date().setDate(new Date().getDate() + 7)]);
    const [startDate, endDate] = dateRange;

    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">Date &amp; Time Picker</h4>
                <p className="text-muted fs-14">
                    A simple date picker using{' '}
                    <Link target="_blank" rel="noreferrer" to={{ pathname: 'https://reactdatepicker.com/' }}>
                        ReactJS Datepicker
                    </Link>
                </p>

                <Row>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Single Date</label> <br />
                            <MintonDatepicker
                                hideAddon={true}
                                value={selectedDate1}
                                onChange={(date) => {
                                    setSelectedDate1(date);
                                }}
                            />
                        </div>
                    </Col>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Single Date with multiple months</label> <br />
                            <MintonDatepicker
                                hideAddon={true}
                                monthsShown={2}
                                value={selectedDate2}
                                onChange={(date) => {
                                    setSelectedDate2(date);
                                }}
                            />
                        </div>
                    </Col>
                </Row>

                <Row>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Custom date format</label> <br />
                            <MintonDatepicker
                                hideAddon={true}
                                dateFormat="yyyy-MM-dd"
                                value={selectedDate3}
                                onChange={(date) => {
                                    setSelectedDate3(date);
                                }}
                            />
                        </div>
                    </Col>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Specific date range</label> <br />
                            <MintonDatepicker
                                selectsRange
                                startDate={startDate}
                                endDate={endDate}
                                hideAddon={true}
                                dateFormat={'yyyy/MM/dd'}
                                onChange={(date) => {
                                    setDateRange(date);
                                }}
                            />
                        </div>
                    </Col>
                </Row>

                <Row>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Select Time</label> <br />
                            <MintonDatepicker
                                hideAddon={true}
                                showTimeSelect
                                timeFormat="HH:mm"
                                tI={60}
                                dateFormat="MMMM d, yyyy h:mm aa"
                                timeCaption="time"
                                value={selectedDate4}
                                onChange={(date) => {
                                    setSelectedDate4(date);
                                }}
                            />
                        </div>
                    </Col>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Time only</label> <br />
                            <MintonDatepicker
                                hideAddon={true}
                                showTimeSelect
                                showTimeSelectOnly
                                tI={60}
                                dateFormat="h:mm aa"
                                timeCaption="Time"
                                value={selectedDate5}
                                onChange={(date) => {
                                    setSelectedDate5(date);
                                }}
                            />
                        </div>
                    </Col>
                </Row>
            </Card.Body>
        </Card>
    );
};

const InputMasks = () => {
    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">Input Masks</h4>
                <p className="text-muted fs-14">
                    Input masks by{' '}
                    <Link
                        target="_blank"
                        rel="noreferrer"
                        to={{ pathname: 'https://github.com/text-mask/text-mask/tree/master/react#readme' }}
                    >
                        react-text-mask
                    </Link>
                </p>

                <Row>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Phone Number with Area Code</label> <br />
                            <MaskedInput
                                mask={[
                                    '(',
                                    /[1-9]/,
                                    /\d/,
                                    ')',
                                    ' ',
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                    '-',
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                ]}
                                placeholder="(__) ____-____"
                                className="form-control"
                            />
                        </div>
                    </Col>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">US Phone Number</label> <br />
                            <MaskedInput
                                mask={[
                                    '(',
                                    /[1-9]/,
                                    /\d/,
                                    /\d/,
                                    ')',
                                    ' ',
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                    '-',
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                    /\d/,
                                ]}
                                placeholder="(___) ___-____"
                                className="form-control"
                            />
                        </div>
                    </Col>
                </Row>

                <Row>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Date</label> <br />
                            <MaskedInput
                                mask={[/\d/, /\d/, '/', /\d/, /\d/, '/', /\d/, /\d/, /\d/, /\d/]}
                                placeholder="__/__/____"
                                className="form-control"
                            />
                        </div>
                    </Col>
                    <Col lg={6}>
                        <div className="mb-3">
                            <label className="form-label">Time</label> <br />
                            <MaskedInput
                                mask={[/\d/, /\d/, ':', /\d/, /\d/, ':', /\d/, /\d/]}
                                placeholder="__:__:__"
                                className="form-control"
                            />
                        </div>
                    </Col>
                </Row>
            </Card.Body>
        </Card>
    );
};

const FormAdvanced = () => {
    return (
        <React.Fragment>
            <PageTitle
                breadCrumbItems={[
                    { label: 'Forms', path: '/forms/advanced' },
                    { label: 'Form Advanced', path: '/forms/advanced', active: true },
                ]}
                title={'Form Advanced'}
            />

            <Row>
                <Col lg={6}>
                    <ReactSelect />
                </Col>
                <Col lg={6}>
                    <Typeaheads />
                </Col>
            </Row>

            <Row>
                <Col xs={12}>
                    <DatePickers />
                </Col>
            </Row>

            <Row>
                <Col xs={12}>
                    <InputMasks />
                </Col>
            </Row>
        </React.Fragment>
    );
};

export default FormAdvanced;
