import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Row, Col, Card, Button, InputGroup, Form } from 'react-bootstrap';
import * as yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';

// components
import PageTitle from '../../components/PageTitle';
import { FormInput, VerticalForm } from '../../components/form';

interface UserData {
    username: string;
    email: string;
    password: string;
    confirmpassword: string;
    checkbox: boolean;
}

interface UserData2 {
    webSite: string;
    email2: string;
    password2: string;
    confirmpassword2: string;
    horizontalCheck: boolean;
}

interface FormData1 {
    requiredInput: string;
    email: string;
    equalTo1: string;
    equalTo2: string;
    url: string;
    digits: string;
    numberInput: string;
    alphaNumeric: string;
    textAreaInput: string;
}

interface FormData2 {
    minLenInput: string;
    maxLenInput: string;
    rangeLenInput: string;
    minValueInput: string;
    maxValueInput: string;
    rangeValueInput: string;
    regEx: string;
}

const NormalFormValidation = () => {
    const [validated, setValidated] = useState<boolean>(false);

    /*
     * handle form submission
     */
    const handleSubmit = (event: any) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }
        setValidated(true);
    };
    return (
        <>
            <Card>
                <Card.Body>
                    <h4 className="header-title">Bootstrap Validation - Normal</h4>
                    <p className="sub-header">
                        Provide valuable, actionable feedback to your users with HTML5 form validationavailable in all
                        our supported browsers.
                    </p>

                    <Form noValidate validated={validated} onSubmit={handleSubmit}>
                        <Form.Group className="mb-3" controlId="validationCustom01">
                            <Form.Label>First name</Form.Label>
                            <Form.Control required type="text" placeholder="First name" defaultValue="Mark" />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="validationCustom02">
                            <Form.Label>Last name</Form.Label>
                            <Form.Control required type="text" placeholder="Last name" defaultValue="Otto" />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="validationCustomUsername">
                            <Form.Label>Username</Form.Label>
