import React from 'react';
import { Card, Col, Row, Button, Placeholder } from 'react-bootstrap';

// components
import PageTitle from '../../components/PageTitle';

// images
import img2 from '../../assets/images/small/img-2.jpg';
import img4 from '../../assets/images/small/img-4.jpg';

const BasicPlaceholders = () => {
    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">Placeholders</h4>
                <p className="sub-header">
                    In the example below, we take a typical card component and recreate it with placeholders applied to
                    create a “loading card”. Size and proportions are the same between the two.
                </p>

                <Row>
                    <Col md={6}>
                        <Card className="border shadow-none mb-md-0">
                            <Card.Img variant="top" src={img2} />
                            <Card.Body>
                                <Card.Title as="h5">Card Title</Card.Title>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of the
                                    card's content.
                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                    <Col md={6}>
                        <Card className="border shadow-none mb-0">
                            <Card.Img variant="top" src={img4} />
                            <Card.Body>
                                <Placeholder as={'h5'} className="card-title" animation="glow">
                                    <Placeholder xs={6} />
                                </Placeholder>
                                <Placeholder as={Card.Text} animation="glow">
                                    <Placeholder xs={7} /> <Placeholder xs={4} /> <Placeholder xs={4} />{' '}
                                    <Placeholder xs={6} /> <Placeholder xs={8} />
                                </Placeholder>
                                <Placeholder.Button variant="primary" disabled xs={6} />
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Card.Body>
        </Card>
    );
};

const PlaceholdersWidth = () => {
    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">Width</h4>
                <p className="sub-header">
                    You can change the <code>width</code> through grid column classes, width utilities, or inline
                    styles.
                </p>
                <Placeholder xs={6} />
                <Placeholder className="w-75" /> <br />
                <Placeholder style={{ width: '25%' }} />
            </Card.Body>
        </Card>
    );
};

const PlaceholdersAnimation = () => {
    return (
        <Card>
            <Card.Body>
                <h4 className="header-title">Animation</h4>
                <p className="sub-header">
                    Animate placeholders by setting the prop <code>animation</code> to <code>glow</code> or{' '}
                    <code>wave</code> to better convey the perception of something being <em>actively</em> loaded.
                </p>

                <Placeholder as="p" animation="glow">
                    <Placeholder xs={12} />
                </Placeholder>
